INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21888, 1185, 'Azithromycin', 'Long QT Syndrome', 'Prolonged cardiac repolarization and QT interval have been reported in patients receiving treatment with azithromycin.  Providers should consider the risk of QT prolongation, which can be fatal, when weighing the risks and benefits of azithromycin for at-risk patients including:  patients with known prolongation of the QT interval, history of torsades de pointes, congenital long QT syndrome, bradyarrhythmias, or uncompensated heart failure; patients with ongoing proarrhythmic conditions such as uncorrected hypokalemia or hypomagnesemia, clinically significant bradycardia, or receiving other drugs that prolong the QT interval.', '2', '', 'DDInter', 0);
