INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22101, 1180, 'Aztreonam', 'Diseases requiring hemodialysis', 'Aztreonam is moderately removed by hemodialysis.  Doses should either be scheduled for administration after dialysis or supplemental doses be given after dialysis.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22102, 31640, 'Aztreonam', 'Diseases requiring hemodialysis', 'Aztreonam is moderately removed by hemodialysis.  Doses should either be scheduled for administration after dialysis or supplemental doses be given after dialysis.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22103, 1180, 'Aztreonam', 'Kidney Diseases', 'Most beta-lactam antibiotics are eliminated by the kidney as unchanged drug and, in some cases, also as metabolites.  The serum concentrations of beta-lactam antibiotics and their metabolites may be increased, and the half-lives prolonged, in patients with impaired renal function.  Neurotoxic reactions (e.g., encephalopathy, asterixis, myoclonus, seizures, coma) have been reported in such patients treated parenterally with these agents.  Dosage adjustments may be necessary, and modifications should be based on the degree of renal impairment as well as severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during prolonged and/or high-dose therapy since nephrotoxicity and alterations in renal function have occasionally been associated with the use of these drugs.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22104, 31640, 'Aztreonam', 'Kidney Diseases', 'Most beta-lactam antibiotics are eliminated by the kidney as unchanged drug and, in some cases, also as metabolites.  The serum concentrations of beta-lactam antibiotics and their metabolites may be increased, and the half-lives prolonged, in patients with impaired renal function.  Neurotoxic reactions (e.g., encephalopathy, asterixis, myoclonus, seizures, coma) have been reported in such patients treated parenterally with these agents.  Dosage adjustments may be necessary, and modifications should be based on the degree of renal impairment as well as severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during prolonged and/or high-dose therapy since nephrotoxicity and alterations in renal function have occasionally been associated with the use of these drugs.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22105, 0, 'Relugolix', 'Osteoporosis', 'Relugolix is contraindicated for use in women with known osteoporosis because of the risk of further bone loss.  Prior to initiating therapy, an assessment of bone mineral density by dual-energy X-ray absorptiometry (DXA) is recommended.  Carefully consider the risks of treatment in women with a history of a low trauma fracture or risk factors for osteoporosis.  Treatment should be limited to 24 months due to risk of continued bone loss which may not be reversible.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22106, 0, 'Relugolix', 'Long QT Syndrome', 'Androgen deprivation therapy may prolong the QT/QTc interval.  Health care providers should consider if benefits of therapy outweigh the potential risks in patients with congenital long QT syndrome, congestive heart failure, electrolyte abnormalities, or taking other drugs that can also prolong the QT interval.  Consider periodic monitoring of EKG and electrolytes.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22107, 1719, 'Reserpine', 'Depressive Disorder', 'The use of rauwolfia alkaloids is contraindicated in patients with a history of mental depression, especially suicidal tendencies.  Rauwolfia alkaloids depletes catecholamine and serotonin stores.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22108, 1719, 'Reserpine', 'Peptic Ulcer', 'The use of rauwolfia alkaloids is contraindicated in patients with active peptic ulcer or ulcerative colitis.  Since rauwolfia alkaloids increase gastrointestinal motility and secretion, these conditions may be aggravated.  Therapy with rauwolfia alkaloids should be administered cautiously in patients with a history of peptic ulcer or ulcerative colitis because of the risk of reactivation.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22109, 1719, 'Reserpine', 'Edema', 'Peripheral- acting antiadrenergic agents can cause peripheral edema.  Therapy with peripheral- acting antiadrenergic agents should be administered cautiously in patients adversely affected by sodium and water retention.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22110, 1719, 'Reserpine', 'Gallstones', 'Rauwolfia alkaloids increase gastrointestinal motility and secretion and can precipitate biliary colic in patients with gallstones.  Therapy with rauwolfia alkaloids should be administered cautiously in patients with a history of gallstones.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22111, 1719, 'Reserpine', 'Kidney Diseases', 'In general, lowering the blood pressure may cause some decrease in renal function.  Therefore, rauwolfia alkaloids should be used with caution in patients with renal impairment, since these patients may adjust poorly to the hypotensive effects of these agents.', '2', '', 'DDInter', 0);
