INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22254, 11915, 'Ribavirin', 'Lung Diseases', 'The use of aerosolized ribavirin has been associated with deterioration in pulmonary function, most significantly in patients with chronic obstructive pulmonary disease or asthma.  Respiratory status should be monitored in all patients receiving aerosolized ribavirin, but in particular, patients with underlying respiratory disorders.  Therapy should be stopped if sudden deterioration occurs, and reinstituted only with extreme caution and consideration of concomitant bronchodilator therapy.', '3', '', 'DDInter', 0);
