INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22277, 11915, 'Ribavirin', 'Kidney Diseases', 'Orally administered ribavirin is metabolized by the liver, and both parent drug and metabolites are eliminated by the kidney.  In patients with creatinine clearance between 10 and 30 mL/min, the mean area under the concentration-time curve has been shown to be three times greater than that in patients with normal renal function, presumably due to decreased clearance of the drug.  Therapy with oral ribavirin should be administered cautiously in patients with creatinine clearance below 50 mL/min.  Dosage adjustments may be necessary.  The drug is not recommended for use in patients with severe renal impairment.', '3', '', 'DDInter', 0);
