INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22386, 21431, 'Ribociclib', 'Stevens-Johnson Syndrome', 'Patients receiving ribociclib have developed severe cutaneous adverse reactions (SCARs), including Stevens-Johnson Syndrome (SJS), toxic epidermal necrolysis (TEN), and drug-induced hypersensitivity syndrome (DiHS)/drug reaction with eosinophilia and systemic symptoms (DRESS).  It is recommended to interrupt treatment if signs or symptoms of severe cutaneous reactions occur until a definitive etiology by a qualified health care professional has been determined.  Permanently discontinue treatment if confirmed SJS, TEN, or DiHS/DRESS and do not reintroduce therapy in patients who have experienced SCARs or other life-threatening cutaneous reactions during treatment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22387, 21432, 'Ribociclib', 'Stevens-Johnson Syndrome', 'Patients receiving ribociclib have developed severe cutaneous adverse reactions (SCARs), including Stevens-Johnson Syndrome (SJS), toxic epidermal necrolysis (TEN), and drug-induced hypersensitivity syndrome (DiHS)/drug reaction with eosinophilia and systemic symptoms (DRESS).  It is recommended to interrupt treatment if signs or symptoms of severe cutaneous reactions occur until a definitive etiology by a qualified health care professional has been determined.  Permanently discontinue treatment if confirmed SJS, TEN, or DiHS/DRESS and do not reintroduce therapy in patients who have experienced SCARs or other life-threatening cutaneous reactions during treatment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22388, 21431, 'Ribociclib', 'Hepatic Insufficiency', 'Ribociclib undergoes extensive hepatic metabolism mainly via CYP450 3A4 in humans.  Increase transaminases have been observed in clinical studies.  It is recommended to reduce the starting dose to 400 mg in patients with moderate (Child-Pugh class B) and severe hepatic impairment (Child-Pugh class C).  Consider performing liver function tests (LFTs) before initiating therapy and monitoring LFTs every 2 weeks for the first 2 cycles, at the beginning of each subsequent 4 cycles, and as clinically indicated.  Dose interruption, reduction, or discontinuation may be necessary based on the severity of LFTs.  No dose adjustment is necessary for patients with mild hepatic impairment (Child-Pugh class A).', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22389, 21432, 'Ribociclib', 'Hepatic Insufficiency', 'Ribociclib undergoes extensive hepatic metabolism mainly via CYP450 3A4 in humans.  Increase transaminases have been observed in clinical studies.  It is recommended to reduce the starting dose to 400 mg in patients with moderate (Child-Pugh class B) and severe hepatic impairment (Child-Pugh class C).  Consider performing liver function tests (LFTs) before initiating therapy and monitoring LFTs every 2 weeks for the first 2 cycles, at the beginning of each subsequent 4 cycles, and as clinically indicated.  Dose interruption, reduction, or discontinuation may be necessary based on the severity of LFTs.  No dose adjustment is necessary for patients with mild hepatic impairment (Child-Pugh class A).', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22390, 21431, 'Ribociclib', 'Neutropenia', 'The use of ribociclib may result in a decrease in neutrophil count.  Care should be exercised when using this drug in neutropenic patients.  It is recommended to perform a complete blood count (CBC) before initiating therapy and to monitor CBC every 2 weeks for the first 2 cycles, at the beginning of each subsequent 4 cycles, and as clinically indicated.  Ribociclib may require dose interruption, reduction or discontinuation of treatment based on the severity of the neutropenia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22391, 21432, 'Ribociclib', 'Neutropenia', 'The use of ribociclib may result in a decrease in neutrophil count.  Care should be exercised when using this drug in neutropenic patients.  It is recommended to perform a complete blood count (CBC) before initiating therapy and to monitor CBC every 2 weeks for the first 2 cycles, at the beginning of each subsequent 4 cycles, and as clinically indicated.  Ribociclib may require dose interruption, reduction or discontinuation of treatment based on the severity of the neutropenia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22392, 21431, 'Ribociclib', 'Kidney Diseases', 'It is recommended to reduce the starting dose to 200 mg in patients with severe renal impairment.  Care is recommended as this drug has not been studied in breast cancer patients with severe renal impairment.  Based on a population pharmacokinetic analysis, no dose adjustment is necessary in patients with mild or moderate renal impairment.', '2', '', 'DDInter', 0);
