INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22403, 14805, 'Rimexolone (ophthalmic)', 'Toxic Optic Neuropathy', 'Prolonged use of corticosteroids may cause posterior subcapsular cataracts and elevated intraocular pressure, the latter of which may lead to glaucoma and/or damage to the optic nerves.  Therapy with ophthalmic corticosteroids should be administered cautiously in patients with a history of cataracts, glaucoma, or increased intraocular pressure.  If these agents are used for more than 10 days, the manufacturers recommend that intraocular pressure be routinely monitored, including in children.  The equatorial and posterior subcapsular portions of the lens should be examined for changes.', '3', '', 'DDInter', 0);
