INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22405, 4364, 'Rimegepant', 'Kidney Diseases', 'Rimegepant should be avoided in patients with ESRD as it not been studied in patients with CrCl less than 15 mL/min or among patients on dialysis.  No dosage adjustment is necessary for patients with mild to severe renal impairment.  A single-dose pharmacokinetic study showed exposure to rimegepant in patients with mild or severe renal impairment was not clinically meaningful different compared to normal subjects, however, exposure in subjects with moderate renal impairment was approximately 40% higher.  In healthy male subjects, 78% of a radiolabeled dose was excreted in the feces and 24% in the urine.', '3', '', 'DDInter', 0);
