INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22407, 4364, 'Rimegepant', 'Liver Failure', 'Rimegepant should be avoided in patients with severe hepatic impairment.  A dedicated pharmacokinetic study has shown an approximately 2-fold increased exposure to a single-dose of rimegepant in patients with severe hepatic impairment (Child-Pugh class C).  There were no clinically meaningful differences in rimegepant exposure with mild (Child-Pugh class A) and moderate hepatic impairment (Child-Pugh class B).', '3', '', 'DDInter', 0);
