INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22409, 9846, 'Riluzole', 'Alcoholism', 'Alcohol consumption may intensify the hepatotoxic potential of riluzole.  Therefore, patients should be warned to limit alcohol intake while receiving riluzole therapy.', '2', '', 'DDInter', 0);
