INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22413, 9846, 'Riluzole', 'Neutropenia', 'The use of riluzole has rarely been associated with neutropenia with an absolute neutrophil count (ANC) less than 500/mm3.  The neutropenia developed within the first 2 months of treatment.  Therapy with riluzole should be administered cautiously in patients predisposed to neutropenia.  Patients should be warned to report any febrile illness.', '2', '', 'DDInter', 0);
