INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22415, 9846, 'Riluzole', 'Kidney Diseases', 'Riluzole and its metabolites are eliminated via the kidney.  Therapy with riluzole should be administered cautiously in patients with renal dysfunction.  Reduced clearance and higher plasma levels may occur in these patients.  Women, native Japanese patients, and the elderly may metabolize or excrete riluzole at a slower rate.  The need for dosage adjustment is not known.', '2', '', 'DDInter', 0);
