INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22429, 4821, 'Risankizumab', 'Tuberculosis', 'Patients should be evaluated for tuberculosis (TB) infection prior to initiating treatment with risankizumab.  Consider anti-TB therapy prior to initiating treatment in patients with a past history of latent or active TB in whom an adequate course of treatment cannot be confirmed.  Monitor patients for signs and symptoms of active TB during and after risankizumab treatment.  Do not administer this medication to patients with active TB.', '2', '', 'DDInter', 0);
