INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22466, 22166, 'Rituximab', 'Infections', 'Rituximab can cause serious, including fatal, bacterial, fungal, and new or reactivated viral infections during and following the completion of rituximab-based therapy.  Infections have been reported in some patients with prolonged hypogammaglobulinemia (defined as hypogammaglobulinemia >11 months after rituximab exposure).  New or reactivated viral infections included cytomegalovirus, herpes simplex virus, parvovirus B19, varicella zoster virus, West Nile virus, and hepatitis B and C.  The manufacturer recommends discontinuing rituximab for serious infections and institute appropriate anti-infective therapy.', '2', '', 'DDInter', 0);
