INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22473, 22166, 'Rituximab', 'Kidney Diseases', 'Severe, including fatal, renal toxicity can occur after rituximab administration in patients with Non-Hodgkin''s Lymphoma (NHL).  Renal toxicity has occurred in patients who experience tumor lysis syndrome and in patients with NHL administered concomitant cisplatin therapy during clinical trials.  The combination of cisplatin and rituximab is not an approved treatment regimen.  Monitor closely for signs of renal failure and discontinue rituximab in patients with a rising serum creatinine or oliguria.  No formal studies have been conducted to examine the effects of renal impairment on the pharmacokinetics of rituximab.', '2', '', 'DDInter', 0);
