INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22484, 22750, 'Rizatriptan', 'Liver Diseases', 'Rizatriptan is primarily metabolized by the liver.  Following oral administration in patients with alcoholic cirrhosis of the liver, the plasma concentrations of rizatriptan were not significantly altered in patients with mild hepatic impairment but were approximately 30% higher in patients with moderate hepatic impairment compared to healthy controls.  Therapy with rizatriptan should be administered cautiously in patients with significantly impaired hepatic function.  A lower initial dosage may be appropriate.', '3', '', 'DDInter', 0);
