INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22488, 22750, 'Rizatriptan', 'Phenylketonurias', 'Maxalt-MLT (brand of rizatriptan) orally disintegrating tablets contain 1.05 mg and 2.10 mg of phenylalanine per each 5 mg and 10 mg tablet, respectively.  The phenylalanine content should be considered when these products are used in patients who must restrict their intake of phenylalanine (i.e. phenylketonurics).', '2', '', 'DDInter', 0);
