INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22490, 22750, 'Rizatriptan', 'Kidney Diseases', 'Rizatriptan is excreted in the urine primarily as metabolites but also as unchanged drug.  Following oral administration in patients with renal impairment (CrCl = 10 to 60 mL/min/1.73 m2), the plasma concentrations of rizatriptan were not significantly altered compared to healthy controls.  In hemodialysis patients (CrCl < 2 mL/min/1.73 m2), however, the area under the plasma concentration-time curve (AUC) was approximately 44% greater than that in controls.  Therapy with rizatriptan should be administered cautiously in dialysis patients and patients with significantly impaired renal function.  A lower initial dosage may be appropriate.', '2', '', 'DDInter', 0);
