INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22553, 9834, 'Rifampicin', 'Endocrine system disease', 'Rifampin has enzyme-inducing effects that can enhance the metabolism of many endogenous substrates, including adrenal hormones, thyroid hormones, and vitamin D, the latter of which may affect serum calcium, phosphate and parathyroid hormone levels.  Patients with preexisting imbalances of these hormones should be monitored more closely during long-term therapy with rifampin.  In patients whose hormonal condition is stabilized on treatment, adjustments may be necessary in their treatment regimen to compensate for these effects.', '2', '', 'DDInter', 0);
