INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (22738, 839, 'Risperidone', 'Liver Diseases', 'Risperidone is metabolized by the liver to the principal active metabolite,  9-hydroxyrisperidone.  Although the pharmacokinetics of risperidone do not seem to be significantly altered in patients with liver disease, the mean free fraction of risperidone in plasma has been shown to increase by about 35% because of the diminished concentration of both albumin and alpha1-acid glycoprotein.  Therapy with risperidone should be administered cautiously in patients with severe hepatic impairment.  Lower initial dosages and slower titration are recommended.', '3', '', 'DDInter', 0);
