INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (23301, 10429, 'Risperidone', 'Parkinsonian Disorders', 'The use of neuroleptic agents is associated with pseudo-parkinsonian symptoms such as akinesia, bradykinesia, tremors, pill-rolling motion, cogwheel rigidity, and postural abnormalities including stooped posture and shuffling gait.  The onset is usually 1 to 2 weeks following initiation of therapy or an increase in dosage.  Older neuroleptic agents such as haloperidol are more likely to induce these effects, and their use may be contraindicated in patients with Parkinson''s disease or parkinsonian symptoms.', '2', '', 'DDInter', 0);
