INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24210, 0, 'Sacituzumab govitecan', 'Hepatic Insufficiency', 'The safety of sacituzumab govitecan has not been established in patients with moderate or severe liver dysfunction; it has not been tested in patients with serum bilirubin greater than 1.5 times the upper limit of normal (1.5 x ULN), or AST and ALT greater than 3 x ULN, or AST and ALT greater than 5 x ULN and associated with liver metastases.  Because the tolerability of sacituzumab govitecan was not investigated in these patients, no recommendations can be made for the starting dose; caution is advised in patients with moderate or severe liver dysfunction.  No adjustment to the starting dose is needed in patients with mild liver dysfunction (bilirubin up to 1.5 x ULN and AST/ALT less than 3 x ULN).', '2', '', 'DDInter', 0);
