INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24244, 3927, 'Ruxolitinib', 'Pancytopenia', 'The use of ruxolitinib can cause thrombocytopenia, anemia, and neutropenia.  It is recommended to manage thrombocytopenia by reducing the dose, temporarily interrupting treatment with ruxolitinib, or as clinically appropriate.  Perform a pre-treatment complete blood count (CBC) and monitor CBCs every 2 to 4 weeks until doses are stabilized, and then as clinically indicated.  Close monitoring is recommended.', '2', '', 'DDInter', 0);
