INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24250, 3927, 'Ruxolitinib', 'Hepatitis B', 'Hepatitis B viral load (HBV-DNA titer) increases, with or without associated elevations in alanine aminotransferase and aspartate aminotransferase, have been reported in patients with chronic HBV infections taking ruxolitinib.  The effect of ruxolitinib on viral replication in patients with chronic HBV infection is unknown.  Care and close monitoring is recommended and patients with chronic HBV infection should be treated and monitored according to clinical guidelines.', '2', '', 'DDInter', 0);
