INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24253, 3927, 'Ruxolitinib', 'Infections', 'Serious bacterial, mycobacterial, fungal and viral infections have been reported in patients receiving ruxolitinib.  It is recommended to delay starting therapy with ruxolitinib until serious active infections have resolved.  Patients should be closely monitored for the development of signs and symptoms of infection during and after treatment.  Treatment should be interrupted if a patient develops a serious infection, an opportunistic infection, or sepsis and appropriate therapy should be instituted according to clinical guidelines.  Prompt management of signs and symptoms of infection is advisable.', '2', '', 'DDInter', 0);
