INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24256, 3927, 'Ruxolitinib', 'Hyperlipidemias', 'The use of ruxolitinib may increase total cholesterol, low-density lipoprotein (LDL) cholesterol, and triglycerides.  It is recommended to assess lipid parameters approximately 8-12 weeks following initiation of therapy and to monitor and treat per clinical guidelines for the management of hyperlipidemia.  Care should be exercised when using this agent in patients with lipids disorders.', '2', '', 'DDInter', 0);
