INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24265, 3927, 'Ruxolitinib', 'Tuberculosis', 'Before initiating ruxolitinib, patients should be screened for latent or active tuberculosis infection.  Prior to initiating therapy with ruxolitinib patients should be evaluated for tuberculosis risk factors, and those at higher risk should be tested for latent infection.  Patients should be closely monitored for any signs and symptoms of tuberculosis, including those patients who tested negative for latent tuberculosis infection before initiating therapy.  Patients with latent tuberculosis should be treated with standard therapy before initiation of treatment and should be closely screened for whether initiating anti-tuberculosis therapy is appropriate.', '2', '', 'DDInter', 0);
