INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24472, 1257, 'Baclofen', 'Epilepsy', 'Deterioration in seizure control and electroencephalographic (EEG) changes have been reported occasionally in epileptic patients treated with baclofen.  Therapy with baclofen should be administered cautiously in patients with a history of seizures.  Clinical status and EEG should be monitored at regular intervals during treatment.  Except in cases of overdose or severe adverse reactions, cessation of baclofen therapy, whenever necessary, should occur gradually with incrementally reduced dosages.  Abrupt withdrawal has been associated with central nervous system effects including seizures, hallucinations, and psychosis.', '2', '', 'DDInter', 0);
