INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24515, 1892, 'Salmon calcitonin', 'Vitamin D Deficiency', 'When calcitonin is used for the treatment of osteoporosis, hypocalcemia and other disturbances of mineral metabolism such as vitamin D deficiency should be treated prior to initiation of therapy.  The desired increase in bone mineral density requires an adequate supply of calcium and vitamin D in the body.  In patients with these conditions, serum calcium and symptoms of hypocalcemia should be monitored during therapy with calcitonin salmon nasal spray.  Appropriate intake of calcium and vitamin D should be ensured throughout the course of treatment.', '2', '', 'DDInter', 0);
