INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24544, 25904, 'Ropinirole', 'Psychotic Disorders', 'Ordinarily, patients with major psychotic disorder should not be treated with dopaminergic antiparkinson agents, because of the risk of exacerbating psychosis.  Hallucinations and psychotic-like behavior have been reported with dopaminergic medications.  In addition, certain medications used to treat psychosis may exacerbate the symptoms of Parkinson''s disease and may decrease the effectiveness of these drugs.', '3', '', 'DDInter', 0);
