INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24576, 25904, 'Ropinirole', 'Kidney Diseases', 'Ropinirole is primarily eliminated by the kidney.  Less than 10% of ropinirole is excreted unchanged in the urine.  No dose adjustment is necessary in patients with moderate renal impairment (CrCl 30 to 50 mL/min).  For patients with end-stage renal disease on hemodialysis, a reduced maximum dose is recommended.', '2', '', 'DDInter', 0);
