INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24626, 9267, 'Saxagliptin', 'Kidney Diseases', 'It is recommended to assess renal function prior to initiating treatment with DPP-4 inhibitors.  Patients with moderate to severe renal insufficiency and end stage renal dysfunction will require a dose adjustment.  Linagliptin will not require a dose adjustment per manufacturer''s information.  Additionally, there have been postmarketing reports of worsening renal function in some patients with renal insufficiency taking sitagliptin at inappropriate doses.  However, sitagliptin has not been found to be nephrotoxic in preclinical studies or clinical trials at appropriate doses.', '2', '', 'DDInter', 0);
