INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24641, 515, 'Secnidazole', 'Alcoholism', 'Nitroimidazoles (e.g., metronidazole, tinidazole, fexinidazole, secnidazole) may inhibit alcohol dehydrogenase and occasionally precipitate a disulfiram-like reaction in patients who consume alcohol while being treated.  Symptoms may include abdominal cramps, nausea, vomiting, headache, flushing, rash, weakness, diarrhea, abdominal pain, dizziness, sweating, and hypotension.  Patients should be instructed to avoid alcohol-containing products during nitroimidazole therapy and for at least 48 hours (fexinidazole, secnidazole) to 72 hours (metronidazole, tinidazole) after the last dose.  Therapy with nitroimidazoles should be administered cautiously in patients who might be prone to acute alcohol intake.  An alternative therapy may be appropriate.', '2', '', 'DDInter', 0);
