INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24667, 24582, 'Selegiline', 'Hypertension', 'Selegiline can cause hypertensive reactions and it should be used with caution in patients with hypertension.  Patients should report any symptoms such as occipital headache, palpitations, neck stiffness or soreness, nausea, sweating, dilated pupils and photophobia.  Patients receiving therapy need to have monitored their blood pressure frequently to detect any evidence of pressor response.  Additionally, patients should be advised to avoid foods and drinks with high tyramine content such as cheese, sour cream, beer, liver, bananas and others, as these might trigger a hypertensive crisis.', '2', '', 'DDInter', 0);
