INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24676, 24582, 'Selegiline', 'Sleep Wake Disorders', 'Selegiline should be used with caution in patients with sleep disorders as it can cause somnolence.  Patients should exercise caution when driving, operating machines, or working at heights during treatment.  Treatment should be discontinued if patients develop daytime sleepiness with episodes of falling asleep during activities such as conversation, eating, etc.  If not discontinued, patients should be very cautious about the activities that they engage in that could be potentially dangerous.  Prescribers should question the patients specifically about somnolence, as it is rarely reported by patients, and ask about sleep disorders and the use of concomitant sedating medications.', '2', '', 'DDInter', 0);
