INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24687, 281, 'Baricitinib', 'Tuberculosis', 'Baricitinib should not be given to patients with active tuberculosis (TB); patients should be evaluated for active infection before administering this agent.  Patients (except those with coronavirus disease 2019 [COVID-19]) should be tested for latent TB before starting baricitinib and during therapy; if positive, patients should be treated with standard antimycobacterial therapy before starting baricitinib.  Anti-TB therapy should be considered prior to initiating treatment with baricitinib in patients with history of latent/active TB in whom an adequate course of treatment cannot be confirmed, and for patients with a negative test for latent TB but who have risk factors for TB infection.  Consultation with a physician with expertise in the treatment of TB is recommended to aid in the decision about whether initiating anti-TB therapy is appropriate for an individual patient.  During baricitinib use, patients should be monitored for signs/symptoms of TB, including patients who tested negative for latent TB infection prior to initiating therapy.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24688, 24035, 'Baricitinib', 'Tuberculosis', 'Baricitinib should not be given to patients with active tuberculosis (TB); patients should be evaluated for active infection before administering this agent.  Patients (except those with coronavirus disease 2019 [COVID-19]) should be tested for latent TB before starting baricitinib and during therapy; if positive, patients should be treated with standard antimycobacterial therapy before starting baricitinib.  Anti-TB therapy should be considered prior to initiating treatment with baricitinib in patients with history of latent/active TB in whom an adequate course of treatment cannot be confirmed, and for patients with a negative test for latent TB but who have risk factors for TB infection.  Consultation with a physician with expertise in the treatment of TB is recommended to aid in the decision about whether initiating anti-TB therapy is appropriate for an individual patient.  During baricitinib use, patients should be monitored for signs/symptoms of TB, including patients who tested negative for latent TB infection prior to initiating therapy.', '3', '', 'DDInter', 0);
