INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24722, 281, 'Baricitinib', 'Hyperlipidemias', 'In controlled clinical trials in patients with rheumatoid arthritis, baricitinib treatment was associated with dose-related increases in lipid parameters including total cholesterol, triglycerides, low-density lipoprotein (LDL) cholesterol, and high-density lipoprotein (HDL) cholesterol.  Lipid parameters should be assessed at baseline and about 12 weeks after initiation of therapy in patients with rheumatoid arthritis or alopecia areata.  Patients should be managed according to clinical guidelines for the management of hyperlipidemia.', '2', '', 'DDInter', 0);
