INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24727, 281, 'Baricitinib', 'Hematologic Diseases', 'Treatment with baricitinib was associated with an increased incidence of neutropenia, lymphopenia, and anemia compared to placebo.  Therapy should not be started or should be interrupted in patients with rheumatoid arthritis or alopecia areata with an absolute lymphocyte count (ALC) less than 500 cells/mm3, absolute neutrophil count (ANC) less than 1000 cells/mm3, or hemoglobin level less than 8 g/dL; therapy should not be started or should be interrupted in patients with coronavirus disease 2019 (COVID-19) with an ALC less than 200 cells/mm3 or ANC less than 500 cells/mm3.  Evaluation at baseline and thereafter according to routine patient management is recommended; dosing should be modified based on ALC, ANC, and/or hemoglobin levels.  Caution is recommended in patients who may be at increased risk.', '2', '', 'DDInter', 0);
