INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24742, 13289, 'Selenium', 'Kidney Diseases', 'The trace metals, chromium and zinc, are excreted primarily in the urine.  Selenium is partially excreted in the urine.  Supplemental doses of these agents may need to be adjusted, reduced, or omitted in patients with renal dysfunction.', '2', '', 'DDInter', 0);
