INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24744, 13289, 'Selenium', 'Malabsorption Syndromes', 'The trace metals manganese, chromium, copper, selenium, and zinc are absorbed in the GI tract from dietary sources and following administration of oral supplements.  GI absorption may be decreased in patients with malabsorption syndromes.  Therefore, larger dosages may be required when these supplements are given orally.  Parenteral administration may be appropriate.', '2', '', 'DDInter', 0);
