INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24775, 22831, 'Sevoflurane', 'Intracranial Hypertension', 'Studies comparing sevoflurane to isoflurane for maintenance of anesthesia during neurosurgical procedures found no difference between sevoflurane and isoflurane with regard to recovery from anesthesia.  In patients at risk for elevations of intracranial pressure (ICP), sevoflurane should be administered cautiously in conjunction with ICP-reducing maneuvers such as hyperventilation.', '2', '', 'DDInter', 0);
