INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24801, 4577, 'Selpercatinib', 'Hypertension', 'Selpercatinib may cause hypertension and therapy should not be initiated in patients with uncontrolled hypertension.  Care should be exercised when using this agent in hypertensive patients.  Optimize blood pressure before starting treatment.  It is recommended to monitor blood pressure after 1 week, and regularly as clinically indicated.  Initiate or adjust anti-hypertensive therapy as appropriate.  Withhold, reduce dose, or permanently discontinue treatment based on the severity.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24802, 4579, 'Selpercatinib', 'Hypertension', 'Selpercatinib may cause hypertension and therapy should not be initiated in patients with uncontrolled hypertension.  Care should be exercised when using this agent in hypertensive patients.  Optimize blood pressure before starting treatment.  It is recommended to monitor blood pressure after 1 week, and regularly as clinically indicated.  Initiate or adjust anti-hypertensive therapy as appropriate.  Withhold, reduce dose, or permanently discontinue treatment based on the severity.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24803, 4577, 'Selpercatinib', 'Liver Diseases', 'Selpercatinib is metabolized predominantly by CYP450 3A4.  The dose of selpercatinib should be reduced in patients with severe hepatic impairment.  No dosage modification is recommended for patients with mild or moderate hepatic impairment.  Serious hepatic adverse reactions have been reported in patients treated with selpercatinib.  Monitor ALT and AST before starting therapy with selpercatinib, every two weeks during the first three months, then monthly thereafter and as clinically indicated.  It is recommended to withhold, reduce dose or permanently discontinue treatment based on the severity of selpercatinib-related adverse reactions in patients with hepatic impairment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24804, 4579, 'Selpercatinib', 'Liver Diseases', 'Selpercatinib is metabolized predominantly by CYP450 3A4.  The dose of selpercatinib should be reduced in patients with severe hepatic impairment.  No dosage modification is recommended for patients with mild or moderate hepatic impairment.  Serious hepatic adverse reactions have been reported in patients treated with selpercatinib.  Monitor ALT and AST before starting therapy with selpercatinib, every two weeks during the first three months, then monthly thereafter and as clinically indicated.  It is recommended to withhold, reduce dose or permanently discontinue treatment based on the severity of selpercatinib-related adverse reactions in patients with hepatic impairment.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24805, 4577, 'Selpercatinib', 'Arrhythmias, Cardiac', 'Selpercatinib can cause concentration-dependent QT interval prolongation.  It is recommended to assess QT interval, electrolytes, and TSH at baseline and periodically during treatment and to adjust treatment based upon risk factors.  Correct hypokalemia, hypomagnesemia, and hypocalcemia before initiating treatment.  Monitor regularly for these laboratory values and correct them as clinically appropriate.  Withhold, reduce the dose or permanently discontinue treatment based on the severity.', '2', '', 'DDInter', 0);
