INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24807, 4577, 'Selpercatinib', 'Kidney Failure, Chronic', 'The use of selpercatinib may increase serum creatinine.  It is recommended to consider alternative markers of renal function if persistent elevations in serum creatinine are observed.  No dosage modification is recommended for patients with mild to moderate renal impairment.  Exercise caution when using this agent in patients with severe renal impairment or end-stage renal disease as the recommended dosage has not been established for these patients.', '2', '', 'DDInter', 0);
