INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24845, 5732, 'Sertraline', 'Glaucoma', 'Some SSRI antidepressants such as fluoxetine, paroxetine and sertraline may have an effect on pupil size causing dilation.  This effect can potentially narrow the eye angle resulting in increased intraocular pressure and angle closure glaucoma, especially in predisposed patients.  These drugs should be used with caution in patients with angle-closure glaucoma or history of glaucoma.', '2', '', 'DDInter', 0);
