INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (24899, 5732, 'Sertraline', 'Blood Platelet Disorders', 'The use of selective serotonin reuptake inhibitors (SSRIs) has been associated with altered platelet function.  Petechiae, purpura, ecchymosis, increased bleeding times, epistaxis and gastrointestinal hemorrhage have been reported.  Therapy with SSRIs should be administered cautiously in patients with severe active bleeding or a hemorrhagic diathesis.', '2', '', 'DDInter', 0);
