INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25416, 1715, 'Sildenafil', 'Retinitis Pigmentosa', 'Phosphodiesterase-5 (PDE5) inhibitors have been associated with transient impairment of color discrimination (blue/green) and blue- or color-tinged vision.  These agents also inhibit phosphodiesterase-6 (PDE6), to a much lesser extent, which is involved in phototransduction in the retina.  There are no controlled clinical data on the safety in patients with retinitis pigmentosa, a minority of whom may have genetic disorders of retinal phosphodiesterases.  Therapy with these agents should be avoided in such patients.', '2', '', 'DDInter', 0);
