INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25627, 30881, 'Siponimod', 'Brain Diseases', 'Rare cases of posterior reversible encephalopathy syndrome (PRES) have been reported in patients receiving a sphingosine 1-phosphate receptor modulator.  It is recommended to promptly schedule a complete physical and neurological examination and should consider an MRI, if a patient develops any unexpected neurological or psychiatric symptoms/signs, any symptom/sign suggestive of an increase of intracranial pressure, or accelerated neurological deterioration.  Delay in diagnosis and treatment may lead to permanent neurological sequelae.  Exercise care when using this agent in patients with a history of ischemic stroke or cerebral hemorrhage.  Treatment should be discontinued if PRES is suspected.', '2', '', 'DDInter', 0);
