INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25630, 30881, 'Siponimod', 'Multiple Sclerosis', 'Rare cases of severe exacerbation of multiple sclerosis (MS), including disease rebound, have been reported after discontinuation of sphingosine 1-phosphate receptor modulator in MS treated patients.  The possibility of severe exacerbation of disease should be considered after stopping treatment with these agents.  Patients should be observed for a severe increase in disability upon discontinuation and appropriate treatment should be instituted, as required.', '2', '', 'DDInter', 0);
