INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25633, 30881, 'Siponimod', 'Infections', 'Siponimod causes a dose-dependent reduction in peripheral lymphocyte count to 20%-30% of baseline values because of reversible sequestration of lymphocytes in lymphoid tissues.  Initiation of treatment should be delayed in patients with severe active infection until resolution.  Before initiating treatment with siponimod, results from a recent complete blood count (i.e., within 6 months or after discontinuation of prior therapy) should be reviewed.  Effective diagnostic and therapeutic strategies should be employed in patients with symptoms of infection while on therapy.  Suspension of treatment should be considered if a patient develops a serious infection.', '2', '', 'DDInter', 0);
