INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25636, 30881, 'Siponimod', 'Liver Diseases', 'Elevations of transaminases may occur in siponimod- treated patients, therefore, transaminase and bilirubin levels should be reviewed before initiation of therapy.  Patients who develop symptoms suggestive of hepatic dysfunction, such as unexplained nausea, vomiting, abdominal pain, fatigue, anorexia, rash with eosinophilia, or jaundice and/or dark urine during treatment, should have liver enzymes checked.  Siponimod should be discontinued if significant liver injury is confirmed.  Although there are no data to establish that patients with preexisting liver disease are at increased risk to develop elevated liver function test values when taking siponimod, caution should be exercised when using the medication in patients with a history of significant liver disease.', '2', '', 'DDInter', 0);
