INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (25736, 84, 'Semaglutide', 'Gastrointestinal Diseases', 'There have been postmarketing reports of acute kidney injury and worsening of chronic renal failure, which may sometimes require hemodialysis, in patients treated with GLP-1 receptor agonists, such as semaglutide.  Some events occurred without underlying renal disease, and the majority of events occurred in patients experiencing gastrointestinal reactions (nausea, vomiting, dehydration).  It is recommended to monitor renal function when initiating or escalating doses of semaglutide in patients reporting severe adverse gastrointestinal reactions.  No dose adjustment is recommended for patients with renal impairment.', '2', '', 'DDInter', 0);
